mapper.select:
select

from cloud_scm.${table.tableName}

	<#list table.columns as column>

		AND ${column.columnName}=${"#"}{${camel(column.columnName)}}

	</#list>

mapper.update:
update cloud_scm.${table.tableName}
<#list table.columns as column> <#if column.primary> <#else>
${column.columnName}=${"#"}{${camel(column.columnName)}}<#if column_has_next>,</#if>
</#if> </#list>
where id=${"#"}{id}

mapper.delete:
delete from cloud_scm.${table.tableName}
WHERE id IN

	${"#"}{item}
